	AND department_profile = #{departmentProfile,jdbcType=VARCHAR}


	AND college_id = #{collegeId,jdbcType=BIGINT}


	AND create_date = #{createDate,jdbcType=DATE}


	AND edit_date = #{editDate,jdbcType=DATE}


	AND del = #{del,jdbcType=INTEGER}

mapper.update:
